package com.teamh.khumon.security;import com.fasterxml.jackson.databind.ObjectMapper;import jakarta.servlet.ServletException;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import lombok.extern.slf4j.Slf4j;import org.json.JSONException;import org.json.JSONObject;import org.springframework.security.core.AuthenticationException;import org.springframework.security.web.authentication.AuthenticationFailureHandler;import org.springframework.stereotype.Component;import java.io.IOException;import java.util.HashMap;import java.util.Map;@Slf4j@Componentpublic class OAuth2LoginFailureHandler implements AuthenticationFailureHandler {    @Override    public void onAuthenticationFailure(HttpServletRequest request, HttpServletResponse response, AuthenticationException exception) throws IOException, ServletException {        ObjectMapper objectMapper = new ObjectMapper();        response.setContentType("application/json;charset=UTF-8");        response.setStatus(HttpServletResponse.SC_BAD_REQUEST);        Map<String, String> jsonObject = new HashMap<>();        log.info("소셜 로그인 실패");        try {            jsonObject.put("message", "소셜 로그인 실패");        } catch (JSONException e) {            throw new RuntimeException(e);        }        response.getWriter().write(objectMapper.writeValueAsString(jsonObject));        response.getWriter().flush();        response.getWriter().close();    }}